package com.wortcook.util;

import java.util.List;
import java.util.Map;

public class HilbertSequence2D {
    public static List<String> SEQUENCES = List.of(
         "A"
        ,"BAAD"
        ,"ABBCBAADBAADCDDA"
        ,"BAADABBCABBCDCCBABBCBAADBAADCDDAABBCBAADBAADCDDADCCBCDDACDDABAAD"
        ,"ABBCBAADBAADCDDABAADABBCABBCDCCBBAADABBCABBCDCCBCDDADCCBDCCBABBCBAADABBCABBCDCCBABBCBAADBAADCDDAABBCBAADBAADCDDADCCBCDDACDDABAADBAADABBCABBCDCCBABBCBAADBAADCDDAABBCBAADBAADCDDADCCBCDDACDDABAADCDDADCCBDCCBABBCDCCBCDDACDDABAADDCCBCDDACDDABAADABBCBAADBAADCDDA"
        ,""
        ,""
        ,""
    );



    private static final Map<String,String> HILBERT_MAP = Map.of(
        "A", "BAAD",
        "B", "ABBC",
        "C", "DCCB",
        "D", "CDDA"
    );

    private static final Map<String,List<int[]>> moves = Map.of(
        "A", List.of(new int[]{0,1}, new int[]{1,0}, new int[]{0,-1}),
        "B", List.of(new int[]{1,0}, new int[]{0,1}, new int[]{-1,0}),
        "C", List.of(new int[]{0,-1}, new int[]{-1,0}, new int[]{0,1}),
        "D", List.of(new int[]{-1,0}, new int[]{0,-1}, new int[]{1,0})
    );

    public static String getHilbertSequence(int depth){
        return getHilbertSequence(depth, "A");
    }

    private static String getHilbertSequence(final int depth, final String sequence){
        if(depth<SEQUENCES.size()){
            return SEQUENCES.get(depth);
        }

        String workingStream = SEQUENCES.get(SEQUENCES.size()-1);

        final StringBuilder sb = new StringBuilder();

        for(int i = SEQUENCES.size(); i <= depth; i++){
            for(int j = 0; j < workingStream.length(); j++){
                sb.append(HILBERT_MAP.get(workingStream.substring(j,j+1)));
            }
            workingStream = sb.toString();
            sb.setLength(0);
        }

        return workingStream;
    }

    public static void main(String[] args){
        String sequence = getHilbertSequence(2);

        int[] position = new int[]{0,0};

        System.out.println(position[0] + " " + position[1]);
        for(int i = 0; i < sequence.length(); i++){
            String move = sequence.substring(i,i+1);
            for(int[] m : moves.get(move)){
                position[0] += m[0];
                position[1] += m[1];
                System.out.println(position[0] + " " + position[1]);
            }
        }

    }
}
